const WelcomeScreen = ({ 
  theme, 
  showXtreamLogin, 
  showM3UModal, 
  onXtreamClick, 
  onM3UClick,
  onXtreamLogin,
  onM3ULoad,
  onCloseXtream,
  onCloseM3U,
  isLoading 
}) => {
  return (
    <div className={`welcome-screen ${theme}`}>
      <div className="welcome-container">
        <h1 className="welcome-title">IPTV Player</h1>
        <p className="welcome-subtitle">Zgjidh burimin e tÃ« dhÃ«nave</p>
        
        <div className="welcome-buttons">
          <button 
            className="welcome-btn xtream" 
            onClick={onXtreamClick}
          >
            <span className="welcome-icon">ðŸ“¡</span>
            <span className="welcome-btn-title">Xtream Codes</span>
            <span className="welcome-btn-desc">Lidhu me server Xtream</span>
          </button>
          
          <button 
            className="welcome-btn m3u" 
            onClick={onM3UClick}
          >
            <span className="welcome-icon">ðŸ“‹</span>
            <span className="welcome-btn-title">M3U Playlist</span>
            <span className="welcome-btn-desc">URL ose skedar lokal</span>
          </button>
        </div>

        {showXtreamLogin && (
          <XtreamLogin
            onLogin={onXtreamLogin}
            onClose={onCloseXtream}
            isLoading={isLoading}
            theme={theme}
          />
        )}

        {showM3UModal && (
          <M3UUploader
            onLoad={onM3ULoad}
            onClose={onCloseM3U}
            isLoading={isLoading}
            theme={theme}
          />
        )}
      </div>
    </div>
  );
};

export default WelcomeScreen;
