import React, { useState, useEffect, useCallback, useRef } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import HlsPlayer from "./components/HlsPlayer";
import SearchModal from "./components/SearchModal";
import SettingsModal from "./components/SettingsModal";
import ErrorBoundary from "./components/ErrorBoundary";
import { getLive, getMovies, getSeries, getCategories, buildStream } from "./services/iptvService";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

// Component për logot me lazy load
const ImageWithLazyLoad = ({ src, alt, className }) => {
  const [isVisible, setIsVisible] = useState(false);
  const imgRef = useRef(null);
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      if (entry.isIntersecting) {
        setIsVisible(true);
        observer.disconnect();
      }
    }, { threshold: 0.1 });

    if (imgRef.current) observer.observe(imgRef.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div ref={imgRef} className={className}>
      {isVisible && !hasError ? (
        <img
          src={src}
          alt={alt}
          className="w-full h-full object-cover"
          loading="lazy"
          onError={(e) => { setHasError(true); e.target.style.display = 'none'; }}
        />
      ) : (
        <div className="w-full h-full bg-gray-700 animate-pulse flex items-center justify-center">
          <span className="text-gray-500 text-xl">{alt ? alt.charAt(0) : '?'}</span>
        </div>
      )}
    </div>
  );
};

// Funksione helper për cache, history dhe optimizim
const optimizeCacheData = (data) => {
  if (!data) return data;
  const optimized = { ...data };
  const maxItems = 500;
  if (optimized.channels?.length > maxItems) optimized.channels = optimized.channels.slice(0, maxItems);
  if (optimized.movies?.length > maxItems) optimized.movies = optimized.movies.slice(0, maxItems);
  if (optimized.series?.length > maxItems) optimized.series = optimized.series.slice(0, maxItems);
  optimized.channels?.forEach(ch => {
    if (ch.logo) ch.logo = ch.logo.replace(/\/\d+$/, '/128');
  });
  return optimized;
};

const limitHistorySize = () => {
  try {
    const history = JSON.parse(localStorage.getItem('play_history') || '[]');
    if (history.length > 50) localStorage.setItem('play_history', JSON.stringify(history.slice(0, 50)));
  } catch (e) { console.error(e); }
};

const cleanupOldCache = () => {
  try {
    const cacheTimestamp = localStorage.getItem('iptv_cache_timestamp');
    if (cacheTimestamp && (Date.now() - parseInt(cacheTimestamp) > 24 * 60 * 60 * 1000)) {
      localStorage.removeItem('iptv_cache');
      localStorage.removeItem('iptv_cache_timestamp');
      console.log('Old cache cleaned up');
    }
  } catch (e) { console.error(e); }
};

// =============================
// APP COMPONENT
// =============================
function App() {
  // Credentials & M3U
  const [server, setServer] = useState(() => localStorage.getItem("server") || "");
  const [user, setUser] = useState(() => localStorage.getItem("user") || "");
  const [pass, setPass] = useState(() => localStorage.getItem("pass") || "");
  const [m3uUrl, setM3uUrl] = useState(() => localStorage.getItem("m3uUrl") || "");

  // Data
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("Të gjitha");
  const [currentStream, setCurrentStream] = useState(null);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [favorites, setFavorites] = useState(() => {
    try { return JSON.parse(localStorage.getItem("favorites")) || []; } catch { return []; }
  });
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("live");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  // UI Modals
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showSidebar, setShowSidebar] = useState(true);

  // Settings
  const [settings, setSettings] = useState(() => ({
    autoPlay: localStorage.getItem("autoPlay") === "true" || false,
    quality: localStorage.getItem("quality") || "auto",
    volume: parseFloat(localStorage.getItem("volume")) || 1.0,
    language: localStorage.getItem("language") || "sq",
    theme: localStorage.getItem("theme") || "dark"
  }));

  const debounceTimer = useRef(null);
  const fetchController = useRef(null);

  // Save settings to localStorage
  useEffect(() => {
    Object.keys(settings).forEach(key => {
      try { localStorage.setItem(key, settings[key]); } catch {}
    });
  }, [settings]);

  // Save credentials debounce
  useEffect(() => {
    if (debounceTimer.current) clearTimeout(debounceTimer.current);
    debounceTimer.current = setTimeout(() => {
      try {
        localStorage.setItem("server", server);
        localStorage.setItem("user", user);
        localStorage.setItem("pass", pass);
        localStorage.setItem("m3uUrl", m3uUrl);
      } catch {}
    }, 500);
  }, [server, user, pass, m3uUrl]);

  // Save favorites
  useEffect(() => {
    try { localStorage.setItem("favorites", JSON.stringify(favorites)); } catch {}
  }, [favorites]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') { e.preventDefault(); setShowSearchModal(true); }
      if (e.key === 'Escape') { setShowSearchModal(false); setShowSettingsModal(false); }
      if (e.key === ' ' && currentStream) { e.preventDefault(); setIsPlaying(!isPlaying); }
      if (e.key === 'f' && currentStream) {
        const player = document.querySelector('.hls-player');
        if (player) {
          if (document.fullscreenElement) document.exitFullscreen();
          else player.requestFullscreen();
        }
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentStream, isPlaying, showSearchModal, showSettingsModal]);

  // Load cache on mount
  useEffect(() => {
    try {
      const cacheData = JSON.parse(localStorage.getItem('iptv_cache') || '{}');
      const cacheTimestamp = localStorage.getItem('iptv_cache_timestamp');
      if (cacheData && cacheTimestamp && Date.now() - parseInt(cacheTimestamp) < 21600000) {
        setChannels(cacheData.channels || []);
        setMovies(cacheData.movies || []);
        setSeries(cacheData.series || []);
        setCategories(cacheData.categories || []);
      }
    } catch {}
    limitHistorySize();
  }, []);

  // =============================
  // FETCH DATA FUNCTION
  // =============================
  const fetchData = async () => {
    setLoading(true);
    if (fetchController.current) fetchController.current.abort();
    fetchController.current = new AbortController();

    setChannels([]);
    setMovies([]);
    setSeries([]);
    setCurrentStream(null);
    setCurrentStreamInfo(null);
    setIsPlaying(false);
    setCategories([]);
    setSelectedCategory("Të gjitha");
    setSearchResults([]);

    try {
      cleanupOldCache();

      if (server && user && pass) {
        const signal = fetchController.current.signal;
        const [live, mov, ser, liveCategories] = await Promise.all([
          getLive(server, user, pass, { signal }).catch(() => []),
          getMovies(server, user, pass, { signal }).catch(() => []),
          getSeries(server, user, pass, { signal }).catch(() => []),
          getCategories(server, user, pass, { signal }).catch(() => [])
        ]);

        setCategories(liveCategories?.map(c => c.category_name) || []);

        setChannels(live.slice(0, 500));
        setMovies(mov.slice(0, 500));
        setSeries(ser.slice(0, 500));

        const cacheData = optimizeCacheData({ channels: live, movies: mov, series: ser, categories: liveCategories });
        localStorage.setItem('iptv_cache', JSON.stringify(cacheData));
        localStorage.setItem('iptv_cache_timestamp', Date.now().toString());

      } else if (m3uUrl) {
        const response = await fetch(m3uUrl);
        const text = await response.text();
        const lines = text.split('\n');
        const live = [];
        lines.forEach((line, i) => {
          if (line.startsWith('#EXTINF:')) {
            live.push({ name: line.split(',')[1], stream_url: lines[i + 1] });
          }
        });
        setChannels(live.slice(0, 500));
      } else {
        throw new Error('As Xtream as M3U URL nuk janë vendosur');
      }
    } catch (e) {
      toast.error(e.message || 'Gabim gjatë ngarkimit');
    } finally {
      setLoading(false);
      fetchController.current = null;
    }
  };

  return (
    <ErrorBoundary>
      <div className={`app-container ${settings.theme}`}>
        <ToastContainer position="top-right" autoClose={3000} />
        {showSidebar && (
          <div className="sidebar">
            <SettingsModal isOpen={showSettingsModal} settings={settings} setSettings={setSettings} onClose={() => setShowSettingsModal(false)} />
            <SearchModal isOpen={showSearchModal} query={searchQuery} setQuery={setSearchQuery} results={searchResults} onClose={() => setShowSearchModal(false)} onSelectStream={(s) => { setCurrentStream(s.stream_url); setIsPlaying(true); setShowSearchModal(false); }} />
          </div>
        )}
        <div className="main-content">
          {activeTab === 'live' && <LiveTVContent channels={channels} currentStream={currentStream} onPlay={(ch) => { setCurrentStream(ch.stream_url); setCurrentStreamInfo(ch); setIsPlaying(true); }} />}
          {activeTab === 'movies' && <MoviesContent movies={movies} onPlay={(mov) => setCurrentStream(mov.stream_url)} />}
          {activeTab === 'series' && <SeriesContent series={series} onPlay={(ser) => setCurrentStream(ser.stream_url)} />}
          {currentStream && <HlsPlayer key={currentStream} src={currentStream} autoPlay={settings.autoPlay} isPlaying={isPlaying} onPlayPause={() => setIsPlaying(!isPlaying)} />}
        </div>
      </div>
    </ErrorBoundary>
  );
}

export default App;
