import React, { useState } from 'react';
import './XtreamLogin.css';

const XtreamLogin = ({ onLogin, onClose, isLoading, theme = 'dark' }) => {
  const [server, setServer] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [saveCredentials, setSaveCredentials] = useState(true);

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin({ server, username, password });
  };

  const handleQuickConnect = (demoServer) => {
    setServer(demoServer);
    setUsername('demo');
    setPassword('demo');
  };

  const isFormValid = server.trim() && username.trim() && password.trim();

  return (
    <div className={`xtream-modal-overlay theme-${theme}`}>
      <div className="xtream-modal">
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üì°</span>
            <h2>Xtream Codes Login</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={onClose}
            disabled={isLoading}
            aria-label="Mbyll"
          >
            √ó
          </button>
        </div>

        <div className="xtream-modal-body">
          <form onSubmit={handleSubmit}>
            {/* Server Input */}
            <div className="xtream-form-group">
              <label htmlFor="server">
                <span className="label-icon">üåê</span>
                Server URL
              </label>
              <input
                id="server"
                type="url"
                className="xtream-form-input"
                placeholder="http://example.com:8080 ose https://example.com"
                value={server}
                onChange={(e) => setServer(e.target.value)}
                disabled={isLoading}
                required
                autoFocus
              />
              <div className="xtream-input-hint">
                P.sh: http://demo.xtream-codes.com:8080
              </div>
            </div>

            {/* Username Input */}
            <div className="xtream-form-group">
              <label htmlFor="username">
                <span className="label-icon">üë§</span>
                Username
              </label>
              <input
                id="username"
                type="text"
                className="xtream-form-input"
                placeholder="Shkruaj username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                disabled={isLoading}
                required
              />
            </div>

            {/* Password Input */}
            <div className="xtream-form-group">
              <label htmlFor="password">
                <span className="label-icon">üîí</span>
                Password
              </label>
              <div className="xtream-password-wrapper">
                <input
                  id="password"
                  type={showPassword ? 'text' : 'password'}
                  className="xtream-form-input"
                  placeholder="Shkruaj password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  disabled={isLoading}
                  required
                />
                <button
                  type="button"
                  className="xtream-password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? 'Fshih password' : 'Shfaq password'}
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            {/* Options */}
            <div className="xtream-form-options">
              <label className="xtream-checkbox">
                <input
                  type="checkbox"
                  checked={saveCredentials}
                  onChange={(e) => setSaveCredentials(e.target.checked)}
                  disabled={isLoading}
                />
                <span className="checkbox-label">Ruaj kredencialet</span>
              </label>
            </div>

            {/* Quick Connect Buttons */}
            <div className="xtream-quick-connect">
              <span className="quick-connect-label">Lidhu shpejt:</span>
              <div className="quick-connect-buttons">
                <button
                  type="button"
                  className="quick-connect-btn"
                  onClick={() => handleQuickConnect('http://demo.xtream-codes.com:8080')}
                  disabled={isLoading}
                >
                  Demo 1
                </button>
                <button
                  type="button"
                  className="quick-connect-btn"
                  onClick={() => handleQuickConnect('http://xtream-demo.com:8080')}
                  disabled={isLoading}
                >
                  Demo 2
                </button>
              </div>
            </div>

            {/* Error Display (n√´se ka) */}
            {/* TODO: Shto error handling */}

            {/* Form Actions */}
            <div className="xtream-form-actions">
              <button
                type="button"
                className="xtream-btn xtream-btn-secondary"
                onClick={onClose}
                disabled={isLoading}
              >
                Anulo
              </button>
              <button
                type="submit"
                className="xtream-btn xtream-btn-primary"
                disabled={!isFormValid || isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke u lidhur...
                  </>
                ) : (
                  'Lidhu'
                )}
              </button>
            </div>
          </form>

          {/* Help Section - KY ESHTE SEKSIONI QE KISHTE GABIMIN */}
          <div className="xtream-help-section">
            <div className="xtream-help-text">
              <span>‚ÑπÔ∏è</span>
              <span>Nuk keni kredenciale?</span>
              <button
                type="button"
                className="xtream-help-link"
                onClick={() => window.open('https://xtream-codes.com', '_blank')}
                disabled={isLoading}
              >
                Merrni kredenciale demo
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default XtreamLogin;
