import React, { useState } from 'react';

const XtreamLogin = ({ onLogin, onClose, isLoading, theme }) => {
  const [server, setServer] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [focusedField, setFocusedField] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!server.trim() || !username.trim() || !password.trim()) {
      setError('T√´ gjitha fushat jan√´ t√´ detyrueshme');
      return;
    }

    try {
      await onLogin({ server: server.trim(), username: username.trim(), password: password.trim() });
    } catch (err) {
      setError(err.message || 'Gabim gjat√´ lidhjes');
    }
  };

  const handleServerExample = () => {
    setServer('http://example.com:8080');
  };

  return (
    <div className="xtream-modal-overlay" onClick={onClose}>
      <div className="xtream-modal" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üì°</span>
            <h2>Xtream Codes Login</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={onClose}
            disabled={isLoading}
          >
            √ó
          </button>
        </div>

        {/* Body */}
        <div className="xtream-modal-body">
          {/* Info Box */}
          <div className="xtream-info-box">
            <span className="info-icon">üí°</span>
            <div className="info-text">
              <strong>Si t√´ lidheni?</strong>
              <p>Vendosni URL-n√´ e serverit, username dhe password</p>
              <button 
                className="info-example" 
                onClick={handleServerExample}
                type="button"
              >
                Shembull: http://example.com:8080
              </button>
            </div>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="xtream-form">
            {/* Server */}
            <div className={`xtream-form-group ${focusedField === 'server' ? 'focused' : ''} ${error && !server ? 'error' : ''}`}>
              <label>
                <span className="field-icon">üåê</span>
                Server URL
              </label>
              <div className="input-wrapper">
                <input
                  type="url"
                  placeholder="http://example.com:8080"
                  value={server}
                  onChange={(e) => setServer(e.target.value)}
                  onFocus={() => setFocusedField('server')}
                  onBlur={() => setFocusedField(null)}
                  disabled={isLoading}
                  autoComplete="off"
                />
                {server && !error && <span className="input-valid">‚úì</span>}
              </div>
            </div>

            {/* Username */}
            <div className={`xtream-form-group ${focusedField === 'username' ? 'focused' : ''} ${error && !username ? 'error' : ''}`}>
              <label>
                <span className="field-icon">üë§</span>
                Username
              </label>
              <div className="input-wrapper">
                <input
                  type="text"
                  placeholder="Username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  onFocus={() => setFocusedField('username')}
                  onBlur={() => setFocusedField(null)}
                  disabled={isLoading}
                  autoComplete="username"
                />
                {username && !error && <span className="input-valid">‚úì</span>}
              </div>
            </div>

            {/* Password */}
            <div className={`xtream-form-group ${focusedField === 'password' ? 'focused' : ''} ${error && !password ? 'error' : ''}`}>
              <label>
                <span className="field-icon">üîí</span>
                Password
              </label>
              <div className="input-wrapper">
                <input
                  type={showPassword ? 'text' : 'password'}
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  onFocus={() => setFocusedField('password')}
                  onBlur={() => setFocusedField(null)}
                  disabled={isLoading}
                  autoComplete="current-password"
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
                {password && !error && <span className="input-valid">‚úì</span>}
              </div>
            </div>

            {/* Error Message */}
            {error && (
              <div className="error-message">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {/* Actions */}
            <div className="xtream-form-actions">
              <button
                type="submit"
                className="xtream-btn xtream-btn-primary"
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke u lidhur...
                  </>
                ) : (
                  <>
                    <span className="btn-icon">üîå</span>
                    Lidhu
                  </>
                )}
              </button>
              
              <button
                type="button"
                className="xtream-btn xtream-btn-secondary"
                onClick={onClose}
                disabled={isLoading}
              >
                <span className="btn-icon">‚úï</span>
                Anulo
              </button>
            </div>
          </form>

          {/* Help Text */}
          <div className="xtream-help-text">
            <span className="help-icon">‚ùì</span>
            <span>Nuk keni kredenciale?</span>
            <button className="help-link" type="button">
              Kontaktoni ofruesin
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default XtreamLogin;
