import React from 'react';

function SeriesContent({ series, playStream, favorites, toggleFavorite, theme, ImageComponent }) {
  if (!series || series.length === 0) {
    return null;
  }

  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
      {series.map((serie) => (
        <div
          key={serie.stream_id}
          className={`rounded-xl overflow-hidden cursor-pointer transition-all duration-300 transform hover:scale-[1.02] hover:shadow-2xl group ${
            theme === 'dark' ? 'bg-gray-800' : 'bg-white shadow'
          }`}
          onClick={() => playStream(serie)}
        >
          <div className="relative aspect-[2/3]">
            <ImageComponent
              src={serie.logo}
              alt={serie.name}
              className="w-full h-full"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                toggleFavorite(serie.stream_id);
              }}
              className="absolute top-2 right-2 bg-black bg-opacity-60 p-1.5 rounded-full z-10"
            >
              {favorites.includes(serie.stream_id) ? (
                <span className="text-yellow-400 text-lg">⭐</span>
              ) : (
                <span className="text-white text-lg">☆</span>
              )}
            </button>
            
            <div className="absolute bottom-0 left-0 right-0 p-3 bg-gradient-to-t from-black to-transparent">
              <div className="flex justify-between items-end">
                <div>
                  <h3 className="font-bold text-white truncate">{serie.name}</h3>
                  <div className="text-xs text-gray-300 mt-1">
                    {serie.category || 'Series'}
                    {serie.seasons && serie.seasons.length > 0 && (
                      <span className="ml-2">Seasons: {serie.seasons.length}</span>
                    )}
                  </div>
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    playStream(serie);
                  }}
                  className="bg-purple-600 hover:bg-purple-500 text-white p-2 rounded-full"
                >
                  ▶️
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default SeriesContent;
