// src/components/VirtualChannelList.jsx
import React, { useMemo } from 'react';
import { FixedSizeGrid as Grid } from 'react-window';
import AutoSizer from 'react-virtualized-auto-sizer';

const VirtualChannelList = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem, 
  theme,
  activeTab 
}) => {
  // Vetëm 20 kanale në ekran njëherësh!
  const COLUMN_COUNT = 5;
  const ROW_HEIGHT = 180;
  const COLUMN_WIDTH = 200;

  const Cell = ({ columnIndex, rowIndex, style }) => {
    const index = rowIndex * COLUMN_COUNT + columnIndex;
    const item = items[index];
    
    if (!item) return null;
    
    const itemId = item.stream_id || item.id || item.series_id;
    const itemName = getItemName(item);
    const itemLogo = getItemLogo(item);
    const isFavorite = favorites.includes(itemId);
    const isSelected = selectedItem?.stream_id === itemId || selectedItem?.id === itemId;
    
    return (
      <div style={style}>
        <div 
          className={`virtual-channel-card ${isSelected ? 'selected' : ''}`}
          onClick={() => onPlay(item)}
        >
          <div className="virtual-card-image">
            <img 
              src={itemLogo} 
              alt={itemName}
              loading="lazy"
            />
          </div>
          <div className="virtual-card-title">{itemName}</div>
          <button
            className={`virtual-card-favorite ${isFavorite ? 'active' : ''}`}
            onClick={(e) => {
              e.stopPropagation();
              onToggleFavorite(itemId);
            }}
          >
            {isFavorite ? '★' : '☆'}
          </button>
        </div>
      </div>
    );
  };

  return (
    <AutoSizer>
      {({ height, width }) => (
        <Grid
          columnCount={COLUMN_COUNT}
          columnWidth={COLUMN_WIDTH}
          height={height}
          rowCount={Math.ceil(items.length / COLUMN_COUNT)}
          rowHeight={ROW_HEIGHT}
          width={width}
          className={`virtual-grid theme-${theme}`}
        >
          {Cell}
        </Grid>
      )}
    </AutoSizer>
  );
};

export default VirtualChannelList;
