import React, { useRef, useEffect, useState, useCallback } from "react";
import Hls from "hls.js";

function HlsPlayer({ 
  src, 
  isPlaying = false, 
  onPlayPause, 
  onClose,
  onError,
  volume = 1 // ‚úÖ Default volume
}) {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [currentVolume, setCurrentVolume] = useState(1); // ‚úÖ 0.0 - 1.0
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  /* =====================
     INITIALIZE PLAYER
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video || !src) return;

    setIsLoading(true);
    setError(null);

    // ‚úÖ KONTROLLO DHE SANITIZE VOLUME
    const safeVolume = () => {
      let vol = currentVolume;
      
      // N√´se volume √´sht√´ undefined, null, NaN, ose jo number
      if (typeof vol !== 'number' || isNaN(vol) || !isFinite(vol)) {
        vol = 1; // Default value
      }
      
      // Kufizo nd√´rmjet 0 dhe 1
      vol = Math.max(0, Math.min(1, vol));
      
      return vol;
    };

    // Vendos volume t√´ sigurt
    try {
      const safeVol = safeVolume();
      video.volume = safeVol;
    } catch (err) {
      console.error("Gabim gjat√´ vendosjes s√´ volumit:", err);
      video.volume = 1; // Force default
    }

    // HLS.js p√´r streamet .m3u8
    if (src.includes('.m3u8')) {
      if (Hls.isSupported()) {
        if (hlsRef.current) {
          hlsRef.current.destroy();
        }

        const hls = new Hls({
          enableWorker: true,
          lowLatencyMode: true,
          backBufferLength: 90
        });

        hlsRef.current = hls;
        hls.loadSource(src);
        hls.attachMedia(video);

        hls.on(Hls.Events.MANIFEST_PARSED, () => {
          setIsLoading(false);
          if (isPlaying) {
            video.play().catch(err => {
              console.error("Play error:", err);
              setError("Nuk mund t√´ filloj√´ playback-u");
            });
          }
        });

        hls.on(Hls.Events.ERROR, (event, data) => {
          if (data.fatal) {
            setError("Gabim n√´ stream");
            onError?.("Gabim n√´ stream");
          }
        });

      } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
        // Safari
        video.src = src;
        video.addEventListener('loadedmetadata', () => {
          setIsLoading(false);
          if (isPlaying) {
            video.play();
          }
        });
      } else {
        setError("HLS nuk mb√´shtetet n√´ k√´t√´ shfletues");
        onError?.("HLS nuk mb√´shtetet");
      }
    } else {
      // Stream normal (MP4, etj)
      video.src = src;
      video.addEventListener('loadedmetadata', () => {
        setIsLoading(false);
        if (isPlaying) {
          video.play();
        }
      });
    }

    // Cleanup
    return () => {
      if (hlsRef.current) {
        hlsRef.current.destroy();
      }
      video.src = "";
    };
  }, [src, onError]);

  /* =====================
     PLAY/PAUSE CONTROL
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isPlaying) {
      video.play().catch(err => {
        console.error("Play error:", err);
        setError("Nuk mund t√´ filloj√´ playback-u");
      });
    } else {
      video.pause();
    }
  }, [isPlaying]);

  /* =====================
     VOLUME CONTROL - FIXI KRYESOR
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    // ‚úÖ SANITIZE VOLUME PARA SE TA VENDOSIM
    let safeVolume = currentVolume;
    
    // Kontrollo n√´se √´sht√´ number
    if (typeof safeVolume !== 'number') {
      safeVolume = parseFloat(safeVolume);
    }
    
    // Kontrollo n√´se √´sht√´ NaN ose Infinity
    if (isNaN(safeVolume) || !isFinite(safeVolume)) {
      safeVolume = 1; // Default
    }
    
    // Kufizo nd√´rmjet 0 dhe 1
    safeVolume = Math.max(0, Math.min(1, safeVolume));

    try {
      video.volume = safeVolume;
      video.muted = isMuted;
    } catch (err) {
      console.error("Gabim gjat√´ vendosjes s√´ volumit:", err);
      // Forco vlerat default
      video.volume = 1;
      video.muted = false;
    }
  }, [currentVolume, isMuted]);

  /* =====================
     TIME UPDATE
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime);
      setDuration(video.duration);
    };

    video.addEventListener('timeupdate', handleTimeUpdate);
    return () => video.removeEventListener('timeupdate', handleTimeUpdate);
  }, []);

  /* =====================
     VOLUME HANDLERS
  ===================== */
  const handleVolumeChange = useCallback((e) => {
    let newVolume = parseFloat(e.target.value);
    
    // ‚úÖ SANITIZE VOLUME
    if (isNaN(newVolume) || !isFinite(newVolume)) {
      newVolume = 1;
    }
    
    newVolume = Math.max(0, Math.min(1, newVolume));
    setCurrentVolume(newVolume);
    
    if (newVolume === 0) {
      setIsMuted(true);
    } else {
      setIsMuted(false);
    }
  }, []);

  const toggleMute = useCallback(() => {
    setIsMuted(prev => !prev);
  }, []);

  /* =====================
     FULLSCREEN
  ===================== */
  const toggleFullscreen = useCallback(() => {
    const video = videoRef.current;
    if (!video) return;

    if (!isFullscreen) {
      if (video.requestFullscreen) {
        video.requestFullscreen();
      } else if (video.webkitRequestFullscreen) {
        video.webkitRequestFullscreen();
      } else if (video.msRequestFullscreen) {
        video.msRequestFullscreen();
      }
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      } else if (document.webkitExitFullscreen) {
        document.webkitExitFullscreen();
      } else if (document.msExitFullscreen) {
        document.msExitFullscreen();
      }
    }
    setIsFullscreen(!isFullscreen);
  }, [isFullscreen]);

  /* =====================
     SEEK
  ===================== */
  const handleSeek = useCallback((e) => {
    const video = videoRef.current;
    if (!video) return;
    
    let time = parseFloat(e.target.value);
    
    // ‚úÖ SANITIZE TIME
    if (isNaN(time) || !isFinite(time)) {
      time = 0;
    }
    
    video.currentTime = Math.max(0, Math.min(time, video.duration || 0));
  }, []);

  /* =====================
     FORMAT TIME
  ===================== */
  const formatTime = (seconds) => {
    if (isNaN(seconds) || !isFinite(seconds)) return "0:00";
    
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = Math.floor(seconds % 60);
    
    if (h > 0) {
      return `${h}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
    }
    return `${m}:${s.toString().padStart(2, '0')}`;
  };

  /* =====================
     RENDER
  ===================== */
  return (
    <div className="hls-player">
      {isLoading && (
        <div className="player-loading">
          <div className="spinner"></div>
          <p>Duke ngarkuar stream...</p>
        </div>
      )}

      {error && (
        <div className="player-error">
          <span>‚ö†Ô∏è {error}</span>
          <button onClick={onClose}>Mbyll</button>
        </div>
      )}

      <video
        ref={videoRef}
        className="video-element"
        onClick={onPlayPause}
        poster="https://via.placeholder.com/1280x720/1a1e24/ffffff?text=IPTV+Player"
      />

      <div className="player-controls">
        {/* Play/Pause Button */}
        <button onClick={onPlayPause} className="control-btn">
          {isPlaying ? "‚è∏Ô∏è" : "‚ñ∂Ô∏è"}
        </button>

        {/* Time Display */}
        <span className="time-display">
          {formatTime(currentTime)} / {formatTime(duration)}
        </span>

        {/* Seek Bar */}
        <input
          type="range"
          className="seek-bar"
          min="0"
          max={duration || 0}
          value={currentTime || 0}
          onChange={handleSeek}
          step="0.1"
        />

        {/* Volume Control - FIXI KRYESOR */}
        <button onClick={toggleMute} className="control-btn">
          {isMuted || currentVolume === 0 ? "üîá" : currentVolume < 0.5 ? "üîâ" : "üîä"}
        </button>
        
        <input
          type="range"
          className="volume-bar"
          min="0"
          max="1"
          step="0.01"
          value={isNaN(currentVolume) || !isFinite(currentVolume) ? 1 : currentVolume}
          onChange={handleVolumeChange}
        />

        {/* Fullscreen Button */}
        <button onClick={toggleFullscreen} className="control-btn">
          {isFullscreen ? "‚õ∂" : "‚õ∂"}
        </button>

        {/* Close Button */}
        <button onClick={onClose} className="control-btn close">
          ‚úï
        </button>
      </div>
    </div>
  );
}

export default HlsPlayer;
