const API_ENDPOINTS = {
  authenticate: '/player_api.php',
  categories: '/player_api.php',
  live: '/player_api.php',
  movies: '/player_api.php',
  series: '/player_api.php',
};

// Funksioni i rregulluar për të ndërtuar stream URL
export const buildStream = (server, user, pass, type, id) => {
  // Pastro URL-në e serverit
  const cleanServer = server.replace(/\/$/, '');
  
  if (type === 'live') {
    return `${cleanServer}/live/${user}/${pass}/${id}.m3u8`;
  } else if (type === 'movie') {
    return `${cleanServer}/movie/${user}/${pass}/${id}.m3u8`;
  } else if (type === 'series') {
    return `${cleanServer}/series/${user}/${pass}/${id}.m3u8`;
  }
  
  return `${cleanServer}/${type}/${user}/${pass}/${id}`;
};

// Opsion alternativ - për servera të ndryshëm
export const buildStreamV2 = (server, user, pass, type, id) => {
  const cleanServer = server.replace(/\/$/, '');
  
  // Formatet e ndryshme të mundshme
  const formats = [
    `${cleanServer}/live/${user}/${pass}/${id}.m3u8`,
    `${cleanServer}/${type}/${user}/${pass}/${id}.m3u8`,
    `${cleanServer}/${type}/${user}/${pass}/${id}`,
    `${cleanServer}/${type}/${user}/${pass}/${id}.ts`,
    `${cleanServer}/${type}/${user}/${pass}/${id}/index.m3u8`
  ];
  
  return formats[0]; // Kthen formatin e parë si parazgjedhje
};

export const getLive = async (server, user, pass) => {
  try {
    const url = `${server}/player_api.php?username=${user}&password=${pass}&action=get_live_streams`;
    console.log("Fetching live from:", url);
    
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }
    
    const data = await response.json();
    console.log("Live data received:", data?.length || 0, "channels");
    return data;
  } catch (error) {
    console.error('Error fetching live:', error);
    return [];
  }
};

