export const buildStreamUrl = (type, id, server, username, password, extension = 'm3u8') => {
  if (!server || !username || !password || !id) {
    throw new Error('MungojnÃ« parametrat pÃ«r Xtream stream');
  }

  let baseUrl = server.replace(/^https?:\/\//, '');
  
  switch (type) {
    case 'live':
      return `http://${baseUrl}/live/${username}/${password}/${id}.${extension}`;
    case 'movie':
      return `http://${baseUrl}/movie/${username}/${password}/${id}.${extension}`;
    case 'series':
      return `http://${baseUrl}/series/${username}/${password}/${id}.${extension}`;
    default:
      return `http://${baseUrl}/live/${username}/${password}/${id}.${extension}`;
  }
};

