  if (!Array.isArray(data)) return [];
  return data.map(item => ({
    ...item,
    start_time: item.start_timestamp ? new Date(item.start_timestamp * 1000).toLocaleTimeString() : '',
    end_time: item.end_timestamp ? new Date(item.end_timestamp * 1000).toLocaleTimeString() : ''
  }));
};

export const formatTime = (timestamp) => {
  if (!timestamp) return '';
  return new Date(timestamp * 1000).toLocaleTimeString('sq-AL', { hour: '2-digit', minute: '2-digit' });
};

export const formatDate = (timestamp) => {
  if (!timestamp) return '';
  return new Date(timestamp * 1000).toLocaleDateString('sq-AL', { 
    weekday: 'long', 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  });
};

