// src/components/VirtualChannelList.jsx - PA DEPENDENCI!
import React, { useState, useEffect, useRef } from 'react'; // âœ… VETÃ‹M useRef, jo FixedSizeGrid!
import { getItemName, getItemLogo, getStreamType } from '../utils/streamHelpers';
import './ChannelList.css';

// Version pa react-window dhe pa AutoSizer
const VirtualChannelList = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem, 
  theme,
  activeTab 
}) => {
  const [visibleItems, setVisibleItems] = useState([]);
  const [page, setPage] = useState(1);
  const observerRef = useRef();
  const lastItemRef = useRef();
  
  const ITEMS_PER_PAGE = 50;

  useEffect(() => {
    setVisibleItems(items.slice(0, ITEMS_PER_PAGE));
    setPage(1);
  }, [items]);

  useEffect(() => {
    if (visibleItems.length >= items.length) return;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          loadMore();
        }
      },
      { threshold: 0.1, rootMargin: '100px' }
    );

    if (lastItemRef.current) {
      observer.observe(lastItemRef.current);
    }

    return () => {
      if (lastItemRef.current) {
        observer.unobserve(lastItemRef.current);
      }
    };
  }, [visibleItems, items]);

  const loadMore = () => {
    if (visibleItems.length >= items.length) return;
    
    const nextPage = page + 1;
    const start = page * ITEMS_PER_PAGE;
    const end = nextPage * ITEMS_PER_PAGE;
    const newItems = items.slice(start, end);
    
    setVisibleItems(prev => [...prev, ...newItems]);
    setPage(nextPage);
  };

  if (!items || items.length === 0) {
    return (
      <div className={`channel-list-empty theme-${theme}`}>
        <div className="empty-icon">ðŸ“º</div>
        <h3>Nuk ka kanale</h3>
        <p>Nuk u gjet asnjÃ« kanal nÃ« kÃ«tÃ« kategori</p>
      </div>
    );
  }

  return (
    <div className={`channel-list theme-${theme}`}>
      <div className="channel-grid">
        {visibleItems.map((item, index) => {
          const itemId = item.stream_id || item.id || item.series_id;
          const itemName = getItemName(item);
          const itemLogo = getItemLogo(item);
          const streamType = getStreamType(item, activeTab);
          const isFavorite = favorites.includes(itemId);
          const isSelected = selectedItem?.stream_id === itemId || selectedItem?.id === itemId;
          const isLastItem = index === visibleItems.length - 1;
          
          return (
            <div
              key={itemId}
              ref={isLastItem ? lastItemRef : null}
              className={`channel-card ${isSelected ? 'selected' : ''}`}
              onClick={() => onPlay(item)}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  onPlay(item);
                }
              }}
            >
              <div className="channel-card-image">
                <img
                  src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`}
                  alt={itemName}
                  loading="lazy"
                  onError={(e) => {
                    e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`;
                  }}
                />
                <div className="channel-card-badge">
                  {streamType === 'live' ? 'LIVE' : 
                   streamType === 'movie' ? 'FILM' : 
                   streamType === 'series' ? 'SERIAL' : 'HD'}
                </div>
              </div>
              
              <div className="channel-card-content">
                <h4 className="channel-card-title">{itemName}</h4>
                <div className="channel-card-footer">
                  <span className="channel-card-category">
                    {item.groupTitle || 'Pa kategori'}
                  </span>
                  <button
                    className={`channel-card-favorite ${isFavorite ? 'active' : ''}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      onToggleFavorite(itemId);
                    }}
                  >
                    {isFavorite ? 'â˜…' : 'â˜†'}
                  </button>
                </div>
              </div>
            </div>
          );
        })}
      </div>
      
      {visibleItems.length < items.length && (
        <div className="loading-more">
          <div className="loading-spinner"></div>
          <span>Duke ngarkuar mÃ« shumÃ« kanale... ({visibleItems.length}/{items.length})</span>
        </div>
      )}
    </div>
  );
};

export default VirtualChannelList;
