import { TABS, STREAM_TYPES } from '../constants/iptv';

// ==================== EKSPORTET KRYESORE ====================

/**
 * Merr emrin e itemit
 */
export function getItemName(item) {
  if (!item) return 'Pa emër';
  
  return item?.name || 
         item?.title || 
         item?.stream_display_name || 
         item?.stream_name ||
         item?.channel_name ||
         'Pa emër';
}

/**
 * Merr ID e itemit
 */
export function getItemId(item) {
  if (!item) return null;
  
  return item?.stream_id || 
         item?.series_id || 
         item?.id || 
         item?.channel_id ||
         null;
}

/**
 * Merr URL e logos
 */
export function getItemLogo(item) {
  if (!item) return null;
  
  return item?.logo || 
         item?.stream_icon || 
         item?.poster || 
         item?.cover || 
         item?.thumbnail || 
         item?.tvg_logo ||
         null;
}

/**
 * Merr tipin e stream-it
 */
export function getStreamType(item, activeTab = TABS.LIVE) {
  if (!item) return STREAM_TYPES.LIVE;
  
  if (item?.type) return item.type;
  
  if (activeTab === TABS.LIVE) return STREAM_TYPES.LIVE;
  if (activeTab === TABS.MOVIES) return STREAM_TYPES.MOVIE;
  if (activeTab === TABS.SERIES) return STREAM_TYPES.SERIES;
  
  return STREAM_TYPES.LIVE;
}

/**
 * Merr kategorinë e itemit
 */
export function getItemCategory(item, activeTab = TABS.LIVE) {
  if (!item) return 'Të tjera';
  
  // Xtream Codes - category_name
  if (item.category_name && item.category_name.trim() !== '') {
    return item.category_name.trim();
  }
  
  // Xtream Codes - category
  if (item.category && item.category.trim() !== '') {
    return item.category.trim();
  }
  
  // M3U - group-title
  if (item.group && item.group.trim() !== '') {
    return item.group.trim();
  }
  
  // M3U - group_title
  if (item.group_title && item.group_title.trim() !== '') {
    return item.group_title.trim();
  }
  
  // Genre per filma/seriale
  if (item.genre && item.genre.trim() !== '') {
    return item.genre.trim();
  }
  
  // Default categories
  if (activeTab === TABS.LIVE) return 'Të tjera';
  if (activeTab === TABS.MOVIES) return 'Filma';
  if (activeTab === TABS.SERIES) return 'Seriale';
  
  return 'Të tjera';
}

/**
 * Merr të gjitha kategoritë
 */
export function getAllCategories(items, activeTab = TABS.LIVE) {
  if (!items || !items.length) return [];
  
  const categories = new Set();
  items.forEach(item => {
    const category = getItemCategory(item, activeTab);
    if (category && category.trim() !== '') {
      categories.add(category);
    }
  });
  
  return Array.from(categories).sort();
}

/**
 * Grupon item-at sipas kategorisë
 */
export function groupItemsByCategory(items, activeTab = TABS.LIVE) {
  if (!items || !items.length) return {};
  
  return items.reduce((acc, item) => {
    const category = getItemCategory(item, activeTab);
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push(item);
    return acc;
  }, {});
}

// ==================== FUNKSIONET NDIHMËSE ====================

/**
 * Kontrollon nëse item është live
 */
export function isLiveStream(item, activeTab) {
  return getStreamType(item, activeTab) === STREAM_TYPES.LIVE;
}

/**
 * Kontrollon nëse item është movie
 */
export function isMovie(item, activeTab) {
  return getStreamType(item, activeTab) === STREAM_TYPES.MOVIE;
}

/**
 * Kontrollon nëse item është series
 */
export function isSeries(item, activeTab) {
  return getStreamType(item, activeTab) === STREAM_TYPES.SERIES;
}

/**
 * Formaton kohën e EPG
 */
export function formatEpgTime(timestamp) {
  if (!timestamp) return '';
  const date = new Date(timestamp * 1000);
  return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
}

/**
 * Formaton datën e EPG
 */
export function formatEpgDate(timestamp) {
  if (!timestamp) return '';
  const date = new Date(timestamp * 1000);
  return date.toLocaleDateString([], { 
    day: '2-digit', 
    month: '2-digit', 
    year: 'numeric' 
  });
}

/**
 * Filtron item-at sipas kërkimit
 */
export function filterItemsBySearch(items, searchQuery) {
  if (!searchQuery || !items.length) return items;
  
  const q = searchQuery.toLowerCase();
  return items.filter(item => 
    getItemName(item).toLowerCase().includes(q) ||
    item?.description?.toLowerCase().includes(q)
  );
}

/**
 * Merr URL e duhur për avatar fallback
 */
export function getAvatarUrl(name, theme = 'dark', size = 48) {
  const bg = theme === 'dark' ? '1e293b' : 'e2e8f0';
  const color = theme === 'dark' ? 'fff' : '0f172a';
  return `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&size=${size}&background=${bg}&color=${color}`;
}

/**
 * Kontrollon nëse item është në favorites
 */
export function isFavorite(item, favorites) {
  const id = getItemId(item);
  return favorites.includes(id);
}

/**
 * Merr statistikat e kategorive
 */
export function getCategoryStats(items, activeTab) {
  const stats = {};
  const grouped = groupItemsByCategory(items, activeTab);
  
  Object.keys(grouped).forEach(category => {
    stats[category] = grouped[category].length;
  });
  
  return stats;
}

/**
 * Sorton item-at sipas emrit
 */
export function sortItemsByName(items, ascending = true) {
  if (!items) return [];
  return [...items].sort((a, b) => {
    const nameA = getItemName(a).toLowerCase();
    const nameB = getItemName(b).toLowerCase();
    if (ascending) {
      return nameA.localeCompare(nameB);
    }
    return nameB.localeCompare(nameA);
  });
}

/**
 * Filtron item-at sipas tipit
 */
export function filterItemsByType(items, type) {
  if (!items || !type) return items;
  return items.filter(item => getStreamType(item) === type);
}

/**
 * Merr përshkrimin e itemit
 */
export function getItemDescription(item) {
  return item?.description || 
         item?.plot || 
         item?.overview || 
         '';
}

/**
 * Merr vitin e itemit (per filma/seriale)
 */
export function getItemYear(item) {
  return item?.year || 
         item?.release_date?.substring(0, 4) || 
         '';
}

/**
 * Merr rating-un e itemit
 */
export function getItemRating(item) {
  return item?.rating || 
         item?.imdb_rating || 
         item?.tmdb_score || 
         null;
}

/**
 * Merr numrin e episodeve (per seriale)
 */
export function getSeriesEpisodes(item) {
  return item?.episodes || 
         item?.episode_count || 
         0;
}

/**
 * Merr numrin e sezoneve (per seriale)
 */
export function getSeriesSeasons(item) {
  return item?.seasons || 
         item?.season_count || 
         0;
}

/**
 * Kontrollon nëse stream-i është i vlefshëm
 */
export function isValidStream(item) {
  if (!item) return false;
  
  const id = getItemId(item);
  const name = getItemName(item);
  const streamUrl = item?.stream_url || item?.url;
  
  return !!(id && name && streamUrl);
}

// ==================== EKSPORTET DEFAULT ====================

const streamHelpers = {
  getItemName,
  getItemId,
  getItemLogo,
  getStreamType,
  getItemCategory,
  getAllCategories,
  groupItemsByCategory,
  isLiveStream,
  isMovie,
  isSeries,
  formatEpgTime,
  formatEpgDate,
  filterItemsBySearch,
  getAvatarUrl,
  isFavorite,
  getCategoryStats,
  sortItemsByName,
  filterItemsByType,
  getItemDescription,
  getItemYear,
  getItemRating,
  getSeriesEpisodes,
  getSeriesSeasons,
  isValidStream
};

export default streamHelpers;
