import React, { useState, useRef } from 'react';

const M3UUploader = ({ onLoad, onClose, isLoading, theme }) => {
  const [sourceType, setSourceType] = useState('url');
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');
  const [focusedField, setFocusedField] = useState(null);
  const fileInputRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (sourceType === 'url') {
      if (!url.trim()) {
        setError('Ju lutem vendosni URL-n√´ e M3U playlist');
        return;
      }
      
      try {
        await onLoad({ type: 'url', url: url.trim() });
      } catch (err) {
        setError(err.message || 'Gabim gjat√´ ngarkimit t√´ M3U');
      }
    } else {
      if (!file) {
        setError('Ju lutem zgjidhni nj√´ skedar M3U');
        return;
      }
      
      try {
        const content = await file.text();
        await onLoad({ type: 'file', content });
      } catch (err) {
        setError(err.message || 'Gabim gjat√´ leximit t√´ skedarit');
      }
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (!selectedFile.name.toLowerCase().endsWith('.m3u') && 
          !selectedFile.name.toLowerCase().endsWith('.m3u8')) {
        setError('Ju lutem zgjidhni nj√´ skedar M3U ose M3U8');
        setFile(null);
        fileInputRef.current.value = '';
        return;
      }
      setFile(selectedFile);
      setError('');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      if (!droppedFile.name.toLowerCase().endsWith('.m3u') && 
          !droppedFile.name.toLowerCase().endsWith('.m3u8')) {
        setError('Ju lutem zgjidhni nj√´ skedar M3U ose M3U8');
        return;
      }
      setFile(droppedFile);
      setError('');
      setSourceType('file');
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleUrlExample = () => {
    setUrl('https://example.com/playlist.m3u');
  };

  return (
    <div className="xtream-modal-overlay" onClick={onClose}>
      <div className="xtream-modal" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üìã</span>
            <h2>M3U Playlist</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={onClose}
            disabled={isLoading}
          >
            √ó
          </button>
        </div>

        {/* Body */}
        <div className="xtream-modal-body">
          {/* Info Box */}
          <div className="xtream-info-box">
            <span className="info-icon">üí°</span>
            <div className="info-text">
              <strong>Ngarko M3U Playlist</strong>
              <p>P√´rdor URL ose ngarko skedar lokal</p>
              <button 
                className="info-example" 
                onClick={handleUrlExample}
                type="button"
              >
                Shembull URL
              </button>
            </div>
          </div>

          {/* Source Type Tabs */}
          <div className="m3u-source-tabs">
            <button
              className={`m3u-source-tab ${sourceType === 'url' ? 'active' : ''}`}
              onClick={() => setSourceType('url')}
              type="button"
            >
              <span className="tab-icon">üîó</span>
              URL
            </button>
            <button
              className={`m3u-source-tab ${sourceType === 'file' ? 'active' : ''}`}
              onClick={() => setSourceType('file')}
              type="button"
            >
              <span className="tab-icon">üìÅ</span>
              Skedar Lokal
            </button>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="xtream-form">
            {sourceType === 'url' ? (
              // URL Input
              <div className={`xtream-form-group ${focusedField === 'url' ? 'focused' : ''} ${error && !url ? 'error' : ''}`}>
                <label>
                  <span className="field-icon">üîó</span>
                  M3U URL
                </label>
                <div className="input-wrapper">
                  <input
                    type="url"
                    placeholder="https://example.com/playlist.m3u"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    onFocus={() => setFocusedField('url')}
                    onBlur={() => setFocusedField(null)}
                    disabled={isLoading}
                    autoComplete="off"
                  />
                  {url && !error && <span className="input-valid">‚úì</span>}
                </div>
              </div>
            ) : (
              // File Upload
              <div 
                className={`m3u-dropzone ${file ? 'has-file' : ''} ${error ? 'error' : ''}`}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onClick={() => fileInputRef.current?.click()}
              >
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".m3u,.m3u8"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                  disabled={isLoading}
                />
                
                {file ? (
                  <>
                    <span className="dropzone-icon">‚úÖ</span>
                    <div className="dropzone-text">
                      <strong>{file.name}</strong>
                      <span>{(file.size / 1024).toFixed(1)} KB</span>
                    </div>
                    <button
                      type="button"
                      className="dropzone-remove"
                      onClick={(e) => {
                        e.stopPropagation();
                        setFile(null);
                        fileInputRef.current.value = '';
                      }}
                    >
                      √ó
                    </button>
                  </>
                ) : (
                  <>
                    <span className="dropzone-icon">üìÇ</span>
                    <div className="dropzone-text">
                      <strong>Kliko ose zvarrit skedar</strong>
                      <span>M3U ose M3U8</span>
                    </div>
                  </>
                )}
              </div>
            )}

            {/* Error Message */}
            {error && (
              <div className="error-message">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {/* Actions */}
            <div className="xtream-form-actions">
              <button
                type="submit"
                className="xtream-btn xtream-btn-primary"
                disabled={isLoading || (sourceType === 'file' && !file)}
              >
                {isLoading ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke ngarkuar...
                  </>
                ) : (
                  <>
                    <span className="btn-icon">üì•</span>
                    Ngarko
                  </>
                )}
              </button>
              
              <button
                type="button"
                className="xtream-btn xtream-btn-secondary"
                onClick={onClose}
                disabled={isLoading}
              >
                <span className="btn-icon">‚úï</span>
                Anulo
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default M3UUploader;
