import React, { useState, useCallback } from 'react';

function M3UUploader({ onLoad, onClose, isLoading }) {
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [activeTab, setActiveTab] = useState('url'); // 'url' or 'file'

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.name.endsWith('.m3u')) {
      setFile(selectedFile);
    } else {
      alert('Zgjidh nj√´ skedar .m3u');
    }
  };

  const handleFileUpload = useCallback(async () => {
    if (!file) return;
    
    try {
      const text = await file.text();
      onLoad({ type: 'file', content: text, name: file.name });
    } catch (err) {
      alert('Gabim gjat√´ leximit t√´ skedarit');
    }
  }, [file, onLoad]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (activeTab === 'url') {
      onLoad({ type: 'url', url });
    } else {
      handleFileUpload();
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content" style={{ maxWidth: '500px' }}>
        <h2>M3U Playlist</h2>
        
        <div className="tab-buttons">
          <button 
            className={`tab-btn ${activeTab === 'url' ? 'active' : ''}`}
            onClick={() => setActiveTab('url')}
          >
            URL
          </button>
          <button 
            className={`tab-btn ${activeTab === 'file' ? 'active' : ''}`}
            onClick={() => setActiveTab('file')}
          >
            Upload Skedar
          </button>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          {activeTab === 'url' ? (
            <>
              <input
                type="url"
                placeholder="https://example.com/playlist.m3u"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                required
              />
              <p className="help-text">
                ‚ö†Ô∏è Shum√´ site bllokojn√´ lidhjet direkte. 
                N√´se nuk funksionon, <strong>shkarko skedarin</strong> dhe ngarko k√´tu.
              </p>
            </>
          ) : (
            <>
              <div className="file-upload-area">
                <input
                  type="file"
                  accept=".m3u,.m3u8,text/plain"
                  onChange={handleFileChange}
                  id="m3u-file"
                  style={{ display: 'none' }}
                />
                <label htmlFor="m3u-file" className="file-label">
                  <span className="file-icon">üìÅ</span>
                  <span>{file ? file.name : 'Zgjidh skedar M3U'}</span>
                </label>
              </div>
              <p className="help-text">
                Shkarko playlist-in nga site-i dhe ngarko k√´tu
              </p>
            </>
          )}

          <div className="modal-actions">
            <button 
              type="submit" 
              disabled={isLoading || (activeTab === 'file' && !file)}
              className="submit-btn"
            >
              {isLoading ? 'Duke ngarkuar...' : 'Ngarko'}
            </button>
            <button type="button" onClick={onClose} className="cancel-btn">
              Anulo
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default M3UUploader;
