// NÃ« App.jsx, shto kÃ«tÃ« funksion para renderit:

const handlePlayerError = useCallback((error) => {
  console.error('ðŸŽ¬ Player error:', error);
  
  let errorMessage = 'Gabim nÃ« player';
  
  if (error) {
    if (typeof error === 'string') {
      errorMessage = error;
    } else if (error.message) {
      errorMessage = error.message;
    } else if (error.type === 'videoError') {
      switch (error.code) {
        case 2:
          errorMessage = 'Gabim nÃ« rrjet. Kontrollo lidhjen.';
          break;
        case 3:
          errorMessage = 'Gabim nÃ« dekodimin e videos.';
          break;
        case 4:
          errorMessage = 'Formati i videos nuk mbÃ«shtetet.';
          break;
        default:
          errorMessage = 'Gabim nÃ« ngarkimin e videos.';
      }
    } else if (error.type === 'hlsError') {
      errorMessage = `Problem me stream-in: ${error.message || error.details || ''}`;
    }
  }
  
  handleError(
    error instanceof Error ? error : new Error(errorMessage), 
    errorMessage
  );
}, [handleError]);

