// components/HlsPlayer.jsx
import React, { useRef, useEffect, useState, useCallback } from "react";
import Hls from "hls.js";

function HlsPlayer({ 
  src, 
  isPlaying = false, 
  onPlayPause, 
  onClose,
  onError,
  volume = 1
}) {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [currentVolume, setCurrentVolume] = useState(1);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const playPromiseRef = useRef(null);

  /* =====================
     INITIALIZE PLAYER
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video || !src) return;

    setIsLoading(true);
    setError(null);

    // Vendos volume t√´ sigurt
    try {
      let safeVolume = currentVolume;
      if (typeof safeVolume !== 'number' || isNaN(safeVolume) || !isFinite(safeVolume)) {
        safeVolume = 1;
      }
      safeVolume = Math.max(0, Math.min(1, safeVolume));
      video.volume = safeVolume;
    } catch (err) {
      video.volume = 1;
    }

    // Funksioni p√´r t√´ luajtur me handle t√´ AbortError
    const playVideo = async () => {
      try {
        // Anulo play-in e m√´parsh√´m n√´se ekziston
        if (playPromiseRef.current) {
          await playPromiseRef.current;
          playPromiseRef.current = null;
        }
        
        if (isPlaying && video) {
          playPromiseRef.current = video.play();
          await playPromiseRef.current;
        }
      } catch (err) {
        // Ignore AbortError - ndodh kur nd√´rrohet stream
        if (err.name === 'AbortError' || err.message?.includes('interrupted')) {
          console.log('‚ÑπÔ∏è Play was interrupted (normal)');
        } else {
          console.error('Play error:', err);
          setError('Nuk mund t√´ filloj√´ playback-u');
          onError?.('Playback error');
        }
      } finally {
        playPromiseRef.current = null;
      }
    };

    // HLS.js p√´r streamet .m3u8
    if (src.includes('.m3u8')) {
      if (Hls.isSupported()) {
        // Shkat√´rro HLS-in e m√´parsh√´m
        if (hlsRef.current) {
          hlsRef.current.destroy();
          hlsRef.current = null;
        }

        const hls = new Hls({
          enableWorker: true,
          lowLatencyMode: true,
          backBufferLength: 90,
          maxBufferLength: 30,
          maxMaxBufferLength: 600,
          startLevel: -1, // Auto
          debug: false
        });

        hlsRef.current = hls;
        hls.loadSource(src);
        hls.attachMedia(video);

        hls.on(Hls.Events.MANIFEST_PARSED, () => {
          setIsLoading(false);
          playVideo();
        });

        hls.on(Hls.Events.ERROR, (event, data) => {
          if (data.fatal) {
            switch (data.type) {
              case Hls.ErrorTypes.NETWORK_ERROR:
                hls.startLoad();
                break;
              case Hls.ErrorTypes.MEDIA_ERROR:
                hls.recoverMediaError();
                break;
              default:
                setError('Gabim n√´ stream');
                onError?.('Stream error');
                break;
            }
          }
        });

      } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
        // Safari
        video.src = src;
        video.addEventListener('loadedmetadata', () => {
          setIsLoading(false);
          playVideo();
        }, { once: true });
      } else {
        setError('HLS nuk mb√´shtetet');
        onError?.('HLS not supported');
      }
    } else {
      // Stream normal (MP4, etj)
      video.src = src;
      video.addEventListener('loadedmetadata', () => {
        setIsLoading(false);
        playVideo();
      }, { once: true });
    }

    // Cleanup
    return () => {
      if (hlsRef.current) {
        hlsRef.current.destroy();
        hlsRef.current = null;
      }
      
      // Anulo play-in n√´se √´sht√´ n√´ progres
      if (playPromiseRef.current) {
        playPromiseRef.current.catch(() => {}); // Ignore errors
        playPromiseRef.current = null;
      }
      
      video.pause();
      video.removeAttribute('src');
      video.load();
    };
  }, [src, onError]);

  /* =====================
     PLAY/PAUSE CONTROL - I P√ãRMIR√ãSUAR
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handlePlay = async () => {
      try {
        if (isPlaying) {
          // Anulo play-in e m√´parsh√´m
          if (playPromiseRef.current) {
            await playPromiseRef.current.catch(() => {});
            playPromiseRef.current = null;
          }
          
          playPromiseRef.current = video.play();
          await playPromiseRef.current;
        } else {
          video.pause();
        }
      } catch (err) {
        if (err.name !== 'AbortError' && !err.message?.includes('interrupted')) {
          console.error('Play/pause error:', err);
        }
      } finally {
        playPromiseRef.current = null;
      }
    };

    handlePlay();

    return () => {
      if (playPromiseRef.current) {
        playPromiseRef.current.catch(() => {});
        playPromiseRef.current = null;
      }
    };
  }, [isPlaying]);

  /* =====================
     VOLUME CONTROL
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    let safeVolume = currentVolume;
    if (typeof safeVolume !== 'number' || isNaN(safeVolume) || !isFinite(safeVolume)) {
      safeVolume = 1;
    }
    safeVolume = Math.max(0, Math.min(1, safeVolume));

    try {
      video.volume = safeVolume;
      video.muted = isMuted;
    } catch (err) {
      console.error('Volume error:', err);
    }
  }, [currentVolume, isMuted]);

  /* =====================
     TIME UPDATE
  ===================== */
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime);
      setDuration(video.duration);
    };

    const handleLoadedMetadata = () => {
      setDuration(video.duration);
    };

    video.addEventListener('timeupdate', handleTimeUpdate);
    video.addEventListener('loadedmetadata', handleLoadedMetadata);
    
    return () => {
      video.removeEventListener('timeupdate', handleTimeUpdate);
      video.removeEventListener('loadedmetadata', handleLoadedMetadata);
    };
  }, []);

  /* =====================
     VOLUME HANDLERS
  ===================== */
  const handleVolumeChange = useCallback((e) => {
    let newVolume = parseFloat(e.target.value);
    if (isNaN(newVolume) || !isFinite(newVolume)) {
      newVolume = 1;
    }
    newVolume = Math.max(0, Math.min(1, newVolume));
    setCurrentVolume(newVolume);
    setIsMuted(newVolume === 0);
  }, []);

  const toggleMute = useCallback(() => {
    setIsMuted(prev => !prev);
  }, []);

  /* =====================
     FULLSCREEN
  ===================== */
  const toggleFullscreen = useCallback(() => {
    const video = videoRef.current;
    if (!video) return;

    if (!document.fullscreenElement) {
      video.requestFullscreen?.();
    } else {
      document.exitFullscreen?.();
    }
    setIsFullscreen(!isFullscreen);
  }, [isFullscreen]);

  /* =====================
     SEEK
  ===================== */
  const handleSeek = useCallback((e) => {
    const video = videoRef.current;
    if (!video) return;
    
    let time = parseFloat(e.target.value);
    if (isNaN(time) || !isFinite(time)) {
      time = 0;
    }
    video.currentTime = Math.max(0, Math.min(time, video.duration || 0));
  }, []);

  /* =====================
     FORMAT TIME
  ===================== */
  const formatTime = (seconds) => {
    if (isNaN(seconds) || !isFinite(seconds)) return "0:00";
    
    const h = Math.floor(seconds / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    const s = Math.floor(seconds % 60);
    
    if (h > 0) {
      return `${h}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
    }
    return `${m}:${s.toString().padStart(2, '0')}`;
  };

  /* =====================
     RENDER
  ===================== */
  return (
    <div className="hls-player">
      {isLoading && (
        <div className="player-loading">
          <div className="spinner"></div>
          <p>Duke ngarkuar stream...</p>
        </div>
      )}

      {error && (
        <div className="player-error">
          <span>‚ö†Ô∏è {error}</span>
          <button onClick={onClose}>Mbyll</button>
        </div>
      )}

      <video
        ref={videoRef}
        className="video-element"
        onClick={onPlayPause}
        poster="https://via.placeholder.com/1280x720/0b1120/ffffff?text=IPTV+Player"
      />

      <div className="player-controls">
        <button onClick={onPlayPause} className="control-btn">
          {isPlaying ? "‚è∏Ô∏è" : "‚ñ∂Ô∏è"}
        </button>

        <span className="time-display">
          {formatTime(currentTime)} / {formatTime(duration)}
        </span>

        <input
          type="range"
          className="seek-bar"
          min="0"
          max={duration || 0}
          value={currentTime || 0}
          onChange={handleSeek}
          step="0.1"
        />

        <button onClick={toggleMute} className="control-btn">
          {isMuted || currentVolume === 0 ? "üîá" : currentVolume < 0.5 ? "üîâ" : "üîä"}
        </button>
        
        <input
          type="range"
          className="volume-bar"
          min="0"
          max="1"
          step="0.01"
          value={currentVolume}
          onChange={handleVolumeChange}
        />

        <button onClick={toggleFullscreen} className="control-btn">
          ‚õ∂
        </button>

        <button onClick={onClose} className="control-btn close">
          ‚úï
        </button>
      </div>
    </div>
  );
}

export default HlsPlayer;
