// App.js - Version i plotë IPTV Player gati për Vercel
import React, { useState, useEffect } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import HlsPlayer from "./components/HlsPlayer";
import SearchModal from "./components/SearchModal";
import SettingsModal from "./components/SettingsModal";
import { getLive, getMovies, getSeries, getCategories, buildStream, fetchM3U } from "./services/iptvService";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  // Credentials
  const [server, setServer] = useState(localStorage.getItem("server") || "");
  const [user, setUser] = useState(localStorage.getItem("user") || "");
  const [pass, setPass] = useState(localStorage.getItem("pass") || "");
  const [m3uUrl, setM3uUrl] = useState(localStorage.getItem("m3uUrl") || "");

  // Data
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState([]);
  const [currentStream, setCurrentStream] = useState(null);
  const [loading, setLoading] = useState(false);

  // UI State
  const [activeTab, setActiveTab] = useState("live");
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [favorites, setFavorites] = useState(() => {
    try { return JSON.parse(localStorage.getItem("favorites")) || []; } catch { return []; }
  });

  // Save credentials/favorites
  useEffect(() => {
    localStorage.setItem("server", server);
    localStorage.setItem("user", user);
    localStorage.setItem("pass", pass);
    localStorage.setItem("m3uUrl", m3uUrl);
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [server, user, pass, m3uUrl, favorites]);

  // Safe API wrapper
  const safeApiCall = async (fn, name) => {
    try {
      const result = await fn();
      if (!result || typeof result !== "object") throw new Error(`Invalid JSON for ${name}`);
      return result;
    } catch (err) {
      toast.error(`Gabim te ${name}: ${err.message}`);
      console.error(`Error in ${name}:`, err);
      return [];
    }
  };

  // Fetch all IPTV data
  const fetchData = async () => {
    if (!server || !user || !pass) return;
    setLoading(true);

    const liveData = await safeApiCall(() => getLive(server,user,pass), "Live Streams");
    const movieData = await safeApiCall(() => getMovies(server,user,pass), "Movies");
    const seriesData = await safeApiCall(() => getSeries(server,user,pass), "Series");
    const catData = await safeApiCall(() => getCategories(server,user,pass), "Categories");

    setChannels(liveData);
    setMovies(movieData);
    setSeries(seriesData);
    setCategories(catData);
    setLoading(false);
  };

  useEffect(() => { fetchData(); }, [server,user,pass]);

  // Play stream
  const playStream = async (type, id) => {
    const url = buildStream(server,user,pass,type,id);
    const m3u = await fetchM3U(url);
    if (m3u) setCurrentStream(url);
    else toast.error("Failed to load stream");
  };

  // Search
  const performSearch = (query) => {
    const all = [...channels, ...movies, ...series];
    const results = all.filter(item =>
      item.name?.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(results);
  };

  // Favorites
  const toggleFavorite = (item) => {
    let updated = [...favorites];
    if (favorites.find(f => f.id === item.id)) {
      updated = favorites.filter(f => f.id !== item.id);
    } else {
      updated.push(item);
    }
    setFavorites(updated);
  };

  return (
    <div className="App">
      {/* Tabs */}
      <div className="tabs">
        <button onClick={() => setActiveTab("live")}>Live TV</button>
        <button onClick={() => setActiveTab("movies")}>Movies</button>
        <button onClick={() => setActiveTab("series")}>Series</button>
        <button onClick={() => setShowSearchModal(true)}>Search</button>
        <button onClick={() => setShowSettingsModal(true)}>Settings</button>
      </div>

      {/* Content */}
      {activeTab === "live" && <LiveTVContent channels={channels} onPlay={playStream} />}
      {activeTab === "movies" && <MoviesContent movies={movies} onPlay={playStream} />}
      {activeTab === "series" && <SeriesContent series={series} onPlay={playStream} />}

      {/* HLS Player */}
      {currentStream && <HlsPlayer url={currentStream} />}

      {/* Settings Modal */}
      {showSettingsModal && (
        <SettingsModal
          server={server} user={user} pass={pass} m3uUrl={m3uUrl}
          onServerChange={setServer} onUserChange={setUser} onPassChange={setPass} onM3uUrlChange={setM3uUrl}
          onFetchData={fetchData}
          onClose={() => setShowSettingsModal(false)}
        />
      )}

      {/* Search Modal */}
      {showSearchModal && (
        <SearchModal
          searchQuery={searchQuery} searchResults={searchResults}
          onSearchChange={(val) => { setSearchQuery(val); performSearch(val); }}
          onPlay={playStream}
          onClose={() => { setShowSearchModal(false); setSearchQuery(""); setSearchResults([]); }}
          favorites={favorites} toggleFavorite={toggleFavorite}
        />
      )}

      <ToastContainer position="bottom-right" autoClose={5000} />
    </div>
  );
}

export default App;
