// src/App.js
import { useState, useEffect } from "react";
import {
  Tv, Film, Layers, Heart, Folder, Settings, Search,
  Play, Pause, Volume2, Maximize, Clock, Home, ChevronRight, ChevronLeft, X, Plus, Trash2, Edit, Globe, Server
} from "lucide-react";
import IPTVPlayer from "./components/IPTVPlayer";
import { fetchChannelsFromM3U } from "./services/iptvService";

export default function App() {
  const [section, setSection] = useState("live");
  const [isPlaying, setIsPlaying] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem("iptvFavorites") || "[]"));
  const [playlists, setPlaylists] = useState(() => JSON.parse(localStorage.getItem("iptvPlaylists") || "[]"));
  const [activePlaylistId, setActivePlaylistId] = useState(() => localStorage.getItem("activePlaylistId") || null);
  const [channels, setChannels] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentStream, setCurrentStream] = useState(null);
  const [showPlaylistModal, setShowPlaylistModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [currentPlaylist, setCurrentPlaylist] = useState({
    id: null, name: "", type: "m3u", url: "", username: "", password: "", serverUrl: ""
  });

  // Toggle favorite
  const toggleFavorite = (itemId) => {
    const newFavorites = favorites.includes(itemId)
      ? favorites.filter(id => id !== itemId)
      : [...favorites, itemId];
    setFavorites(newFavorites);
    localStorage.setItem("iptvFavorites", JSON.stringify(newFavorites));
  };

  // Load channels from playlist
  const loadChannels = async (playlistId) => {
    const playlist = playlists.find(p => p.id === playlistId);
    if (!playlist) return;
    setIsLoading(true);
    setCurrentStream(null);

    try {
      let m3uUrl = playlist.url;
      if (playlist.type === "xtream") {
        const cleanServer = playlist.serverUrl.replace(/\/$/, '');
        m3uUrl = `${cleanServer}/get.php?username=${playlist.username}&password=${playlist.password}&type=m3u_plus`;
      }

      const fetchedChannels = await fetchChannelsFromM3U(m3uUrl);
      const validChannels = fetchedChannels.filter(ch => ch.streamUrl?.trim() !== '');
      setChannels(validChannels);
      setActivePlaylistId(playlistId);
      localStorage.setItem("activePlaylistId", playlistId);

      if (validChannels.length > 0) {
        setCurrentStream(validChannels[0].streamUrl);
        setIsPlaying(true);
      }
    } catch (error) {
      console.error("Gabim:", error);
      alert(`Gabim: ${error.message}\nKontrollo URL-në/llogarinë dhe internetin`);
    } finally {
      setIsLoading(false);
    }
  };

  // Save playlist
  const savePlaylist = () => {
    if (!currentPlaylist.name.trim()) return alert("Vendos emër për listën!");
    if (currentPlaylist.type === "m3u" && !currentPlaylist.url.trim()) return alert("Vendos URL për listën M3U!");
    if (currentPlaylist.type === "xtream" && (!currentPlaylist.serverUrl || !currentPlaylist.username || !currentPlaylist.password)) return alert("Plotëso të gjitha fushat për Xtream!");

    let newPlaylists = [...playlists];
    if (modalMode === "add") {
      newPlaylists.push({ ...currentPlaylist, id: Date.now().toString(), createdAt: new Date().toISOString() });
    } else {
      newPlaylists = newPlaylists.map(p => p.id === currentPlaylist.id ? { ...currentPlaylist } : p);
    }

    setPlaylists(newPlaylists);
    localStorage.setItem("iptvPlaylists", JSON.stringify(newPlaylists));

    if (modalMode === "add" && newPlaylists.length === 1) loadChannels(newPlaylists[0].id);

    setShowPlaylistModal(false);
    setCurrentPlaylist({ id: null, name: "", type: "m3u", url: "", username: "", password: "", serverUrl: "" });
  };

  // Delete playlist
  const deletePlaylist = (playlistId, e) => {
    e?.stopPropagation();
    if (!confirm("Fshi këtë listë?")) return;
    const newPlaylists = playlists.filter(p => p.id !== playlistId);
    setPlaylists(newPlaylists);
    localStorage.setItem("iptvPlaylists", JSON.stringify(newPlaylists));

    if (activePlaylistId === playlistId) {
      setActivePlaylistId(null);
      setChannels([]);
      setCurrentStream(null);
      localStorage.removeItem("activePlaylistId");
    }
  };

  // Edit playlist
  const editPlaylist = (playlist, e) => {
    e?.stopPropagation();
    setCurrentPlaylist(playlist);
    setModalMode("edit");
    setShowPlaylistModal(true);
  };

  useEffect(() => {
    if (activePlaylistId) loadChannels(activePlaylistId);
  }, []);

  const activePlaylist = playlists.find(p => p.id === activePlaylistId);

  // Filtered channels based on search query
  const filteredChannels = channels.filter(channel =>
    channel.name?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="flex h-screen bg-gradient-to-br from-[#0b0f14] to-[#1a1f2e] text-white">
      {/* Sidebar */}
      <aside className="w-64 bg-gradient-to-b from-[#111827] to-[#1f2937] flex flex-col p-4 gap-2 border-r border-gray-800">
        <div className="flex items-center gap-3 mb-6 p-2">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
            <Tv size={24} />
          </div>
          <h1 className="text-xl font-bold">IPTV Player</h1>
        </div>
        <NavItem icon={<Tv />} label="Live TV" active={section === "live"} onClick={() => setSection("live")} />
        <NavItem icon={<Film />} label="Movies" active={section === "movies"} onClick={() => setSection("movies")} />
        <NavItem icon={<Layers />} label="Series" active={section === "series"} onClick={() => setSection("series")} />
        <NavItem icon={<Heart />} label="Favorites" active={section === "fav"} onClick={() => setSection("fav")} badge={favorites.length} />
        <NavItem icon={<Folder />} label="Playlists" active={section === "lists"} onClick={() => setSection("lists")} badge={playlists.length} />
        <div className="mt-auto pt-4 border-t border-gray-800">
          <NavItem icon={<Settings />} label="Settings" active={section === "settings"} onClick={() => setSection("settings")} />
        </div>
      </aside>

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <header className="p-4 border-b border-gray-800 bg-gradient-to-r from-[#111827]/80 to-[#1f2937]/80 backdrop-blur-sm">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><ChevronLeft size={20} /></button>
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><ChevronRight size={20} /></button>
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><Home size={20} /></button>
              <div className="relative w-96">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Kërko kanale..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 bg-gray-900/50 border border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
            <div className="flex items-center gap-3">
              <div className="text-sm">
                {activePlaylist ? (
                  <div className="flex items-center gap-2">
                    <div className={`p-1 rounded ${activePlaylist.type === 'm3u' ? 'bg-blue-500/20' : 'bg-green-500/20'}`}>
                      {activePlaylist.type === 'm3u' ? <Globe size={14} /> : <Server size={14} />}
                    </div>
                    <span className="text-green-400">{activePlaylist.name}</span>
                    <span className="text-gray-400">({channels.length})</span>
                  </div>
                ) : (
                  <div className="flex items-center gap-2">
                    <X size={16} className="text-red-500" />
                    <span className="text-red-400">Asnjë listë</span>
                  </div>
                )}
              </div>
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><Clock size={20} /></button>
            </div>
          </div>
        </header>

        <main className="flex-1 p-6 overflow-y-auto">
          {section === "live" && (
            <LiveTVContent
              channels={filteredChannels}
              favorites={favorites}
              toggleFavorite={toggleFavorite}
              currentStream={currentStream}
              setCurrentStream={setCurrentStream}
              isLoading={isLoading}
              isPlaying={isPlaying}
              setIsPlaying={setIsPlaying}
              loadChannels={loadChannels}
              playlists={playlists}
              activePlaylist={activePlaylist}
              setShowPlaylistModal={setShowPlaylistModal}
              setModalMode={setModalMode}
            />
          )}
          {section === "movies" && <MoviesContent favorites={favorites} toggleFavorite={toggleFavorite} />}
          {section === "series" && <SeriesContent favorites={favorites} toggleFavorite={toggleFavorite} />}
          {section === "fav" && <FavoritesContent favorites={favorites} toggleFavorite={toggleFavorite} channels={channels} />}
          {section === "lists" && <PlaylistsContent playlists={playlists} activePlaylistId={activePlaylistId} loadChannels={loadChannels} deletePlaylist={deletePlaylist} editPlaylist={editPlaylist} setShowPlaylistModal={setShowPlaylistModal} setModalMode={setModalMode} />}
          {section === "settings" && <SettingsContent playlists={playlists} activePlaylistId={activePlaylistId} loadChannels={loadChannels} />}
        </main>

        {/* Player footer */}
        <div className="border-t border-gray-800 bg-[#111827] p-4">
          <div className="flex items-center justify-between max-w-4xl mx-auto">
            <div className="flex items-center gap-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Tv size={20} />
              </div>
              <div>
                <div className="font-medium">
                  {currentStream ? channels.find(c => c.streamUrl === currentStream)?.name || "Kanal" : "Zgjidh kanal"}
                </div>
                <div className="text-sm text-gray-400">{currentStream ? "Live" : "Player IPTV"}</div>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><ChevronLeft size={20} /></button>
              <button onClick={() => setIsPlaying(!isPlaying)} className="p-3 bg-blue-600 hover:bg-blue-700 rounded-xl transition">
                {isPlaying ? <Pause size={20} /> : <Play size={20} />}
              </button>
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><ChevronRight size={20} /></button>
            </div>
            <div className="flex items-center gap-4">
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><Volume2 size={20} /></button>
              <button className="p-2 hover:bg-gray-800 rounded-lg transition"><Maximize size={20} /></button>
            </div>
          </div>
        </div>
      </div>

      {/* Playlist Modal */}
      {showPlaylistModal && (
        <PlaylistModal
          currentPlaylist={currentPlaylist}
          setCurrentPlaylist={setCurrentPlaylist}
          modalMode={modalMode}
          savePlaylist={savePlaylist}
          closeModal={() => {
            setShowPlaylistModal(false);
            setCurrentPlaylist({ id: null, name: "", type: "m3u", url: "", username: "", password: "", serverUrl: "" });
          }}
        />
      )}
    </div>
  );
}

// ---------- Helper Components ----------

function NavItem({ icon, label, active, onClick, badge }) {
  return (
    <div
      className={`flex items-center gap-3 p-2 rounded-lg cursor-pointer transition ${active ? "bg-blue-600/30" : "hover:bg-gray-800"}`}
      onClick={onClick}
    >
      {icon} <span className="flex-1">{label}</span> {badge && <span className="text-xs bg-red-600 rounded-full px-2 py-0.5">{badge}</span>}
    </div>
  );
}

// ---------- Placeholder Components ----------
function LiveTVContent({ channels, favorites, toggleFavorite, currentStream, setCurrentStream, isLoading, isPlaying }) {
  return (
    <div>
      {isLoading ? (
        <div className="text-gray-400">Ngarko kanalet...</div>
      ) : (
        <div className="grid grid-cols-4 gap-4">
          {channels.map(ch => (
            <div key={ch.streamUrl} className="bg-gray-800 p-3 rounded-lg cursor-pointer hover:bg-gray-700" onClick={() => setCurrentStream(ch.streamUrl)}>
              <div className="flex justify-between items-center mb-2">
                <span>{ch.name}</span>
                <Heart size={16} className={`cursor-pointer ${favorites.includes(ch.streamUrl) ? 'text-red-500' : 'text-gray-400'}`} onClick={(e) => { e.stopPropagation(); toggleFavorite(ch.streamUrl); }} />
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
function MoviesContent({ favorites, toggleFavorite }) { return <div>Filma</div>; }
function SeriesContent({ favorites, toggleFavorite }) { return <div>Seriale</div>; }
function FavoritesContent({ favorites, toggleFavorite, channels }) { return <div>Favorites</div>; }
function PlaylistsContent({ playlists, activePlaylistId, loadChannels, deletePlaylist, editPlaylist, setShowPlaylistModal, setModalMode }) { return <div>Playlists</div>; }
function SettingsContent({ playlists, activePlaylistId, loadChannels }) { return <div>Settings</div>; }
function PlaylistModal({ currentPlaylist, setCurrentPlaylist, modalMode, savePlaylist, closeModal }) {
  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
      <div className="bg-gray-900 p-6 rounded-xl w-96">
        <h2 className="text-xl font-bold mb-4">{modalMode === "add" ? "Shto Listë" : "Edito Listë"}</h2>
        <input type="text" placeholder="Emri" className="w-full p-2 mb-3 rounded bg-gray-800" value={currentPlaylist.name} onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, name: e.target.value })} />
        <input type="text" placeholder="URL" className="w-full p-2 mb-3 rounded bg-gray-800" value={currentPlaylist.url} onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, url: e.target.value })} />
        <div className="flex justify-end gap-3">
          <button className="px-4 py-2 bg-gray-700 rounded" onClick={closeModal}>Mbyll</button>
          <button className="px-4 py-2 bg-blue-600 rounded" onClick={savePlaylist}>{modalMode === "add" ? "Shto" : "Ruaj"}</button>
        </div>
      </div>
    </div>
  );
}
