import React from 'react';
import XtreamLogin from './XtreamLogin';
import M3UUploader from './M3UUploader';
import './WelcomeScreen.css';

const WelcomeScreen = ({ 
  theme,
  onXtreamClick,
  onM3UClick,
  onXtreamLogin,
  onM3ULoad,
  onCloseXtream,
  onCloseM3U,
  isLoading,
  showXtreamLogin,
  showM3UModal
}) => {
  return (
    <div className={`welcome-screen theme-${theme}`} data-theme={theme}>
      <div className="welcome-container">
        <h1 className="welcome-title">IPTV Player</h1>
        <p className="welcome-subtitle">
          Zgjidhni burimin pÃ«r tÃ« filluar
        </p>

        <div className="welcome-buttons">
          {/* Xtream Codes Button */}
          <button 
            className="welcome-btn xtream"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              console.log('Xtream button clicked');
              onXtreamClick();
            }}
            disabled={isLoading}
            type="button"
          >
            <span className="welcome-icon">ðŸ“¡</span>
            <span className="welcome-btn-title">Xtream Codes</span>
            <span className="welcome-btn-desc">
              Lidhu me server Xtream Codes
            </span>
          </button>

          {/* M3U Button */}
          <button 
            className="welcome-btn m3u"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              console.log('M3U button clicked');
              onM3UClick();
            }}
            disabled={isLoading}
            type="button"
          >
            <span className="welcome-icon">ðŸ“‹</span>
            <span className="welcome-btn-title">M3U Playlist</span>
            <span className="welcome-btn-desc">
              Ngarko playlist nga skedar ose URL
            </span>
          </button>
        </div>

        {/* Modals - OUTSIDE welcome-buttons */}
        {showXtreamLogin && (
          <div className="modal-wrapper">
            <XtreamLogin
              onLogin={onXtreamLogin}
              onClose={() => {
                console.log('Closing Xtream modal');
                onCloseXtream();
              }}
              isLoading={isLoading}
              theme={theme}
            />
          </div>
        )}

        {showM3UModal && (
          <div className="modal-wrapper">
            <M3UUploader
              onLoad={onM3ULoad}
              onClose={() => {
                console.log('Closing M3U modal');
                onCloseM3U();
              }}
              isLoading={isLoading}
              theme={theme}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default WelcomeScreen;
